import java.util.Scanner; public class Main {public static void divideFunc(String strValue) {String[] strArray = strValue.split(" "); int firstNumber = Integer.parseInt(strArray[0]); int secondNumber = Integer.parseInt(strArray[1]); System.out.println(firstNumber / secondNumber);} public static void main(String[] args) {Scanner scanner = new Scanner(System.in); String line = scanner.nextLine(); divideFunc(line);}}